(function(){
  
//JavaScript Para Scroll Suave Federico Carrizo
var defaultOptions = {

    // Nucleo de Dezplanamiento
    frameRate        : 150, // [Hz]
    animationTime    : 1800, // [px]
    stepSize         : 85, // [px]

    // Pulso(No Tocar)
    // relación de "cola" a "aceleración"
    pulseAlgorithm   : true,
    pulseScale       : 8,
    pulseNormalize   : 1,

    // Aceleracion
    accelerationDelta : 20,  // 20
    accelerationMax   : 1,   // 1

    // Configuraciones del teclado
    keyboardSupport   : true,  // option
    arrowScroll       : 50,     // [px]

    // Otras(Para prevenir errores de carga y bugs)
    touchpadSupport   : true,
    fixedBackground   : true, 
    excluded          : ""    
};

var options = defaultOptions;


// Otras Variables
var isExcluded = false;
var isFrame = false;
var direction = { x: 0, y: 0 };
var initDone  = false;
var root = document.documentElement;
var activeElement;
var observer;
var deltaBuffer = [ 120, 120, 120 ];

var key = { left: 37, up: 38, right: 39, down: 40, spacebar: 32, 
            pageup: 33, pagedown: 34, end: 35, home: 36 };


/***********************************************
 * Opciones * * Federico Carrizo - Konnor Desing's *
 ***********************************************/

var options = defaultOptions;


/***********************************************
 * Inicializar
 ***********************************************/

/**
 * Prueba si se permite el desplazamiento suave. Apaga todo sino, no
 */
function initTest() {

    var disableKeyboard = false; 
    
    // deshabilitar el soporte del teclado si algo más arriba lo solicitó
    if (disableKeyboard) {
        removeEvent("keydown", keydown);
    }

    if (options.keyboardSupport && !disableKeyboard) {
        addEvent("keydown", keydown);
    }
}

/**
 * Configura despliegues de matriz, determina si los marcos están involucrados.
 */
function init() {
  
    if (!document.body) return;

    var body = document.body;
    var html = document.documentElement;
    var windowHeight = window.innerHeight; 
    var scrollHeight = body.scrollHeight;
    
    // comprueba el modo de compatibilidad para el elemento raíz
    root = (document.compatMode.indexOf('CSS') >= 0) ? html : body;
    activeElement = body;
    
    initTest();
    initDone = true;

    // Comprueba si este script se ejecuta en un marco
    if (top != self) {
        isFrame = true;
    }

    /**
     * Esto corrige un error donde las áreas a la izquierda y a la derecha
     * el contenido no desencadena el evento onmousewheel
     * en algunas páginas p. ej .: html, cuerpo {altura: 100%}
     */
    else if (scrollHeight > windowHeight &&
            (body.offsetHeight <= windowHeight || 
             html.offsetHeight <= windowHeight)) {

        html.style.height = 'auto';
        //setTimeout(refresh, 10);

        // clearfix
        if (root.offsetHeight <= windowHeight) {
            var underlay = document.createElement("div"); 	
            underlay.style.clear = "both";
            body.appendChild(underlay);
        }
    }

    // deshabilitar fondo fijo
    if (!options.fixedBackground && !isExcluded) {
        body.style.backgroundAttachment = "scroll";
        html.style.backgroundAttachment = "scroll";
    }
}


/************************************************
 * DESPLAZAMIENTO
 ************************************************/
 
var que = [];
var pending = false;
var lastScroll = +new Date;

/**
 * Empuja acciones de desplazamiento a la cola de desplazamiento.
 */
function scrollArray(elem, left, top, delay) {
    
    delay || (delay = 1000);
    directionCheck(left, top);

    if (options.accelerationMax != 1) {
        var now = +new Date;
        var elapsed = now - lastScroll;
        if (elapsed < options.accelerationDelta) {
            var factor = (1 + (30 / elapsed)) / 2;
            if (factor > 1) {
                factor = Math.min(factor, options.accelerationMax);
                left *= factor;
                top  *= factor;
            }
        }
        lastScroll = +new Date;
    }          
    
    // empujar un comando de desplazamiento
    que.push({
        x: left, 
        y: top, 
        lastX: (left < 0) ? 0.99 : -0.99,
        lastY: (top  < 0) ? 0.99 : -0.99, 
        start: +new Date
    });
        
    // no actua si hay una cola pendiente
    if (pending) {
        return;
    }  

    var scrollWindow = (elem === document.body);
    
    var step = function (time) {
        
        var now = +new Date;
        var scrollX = 0;
        var scrollY = 0; 
    
        for (var i = 0; i < que.length; i++) {
            
            var item = que[i];
            var elapsed  = now - item.start;
            var finished = (elapsed >= options.animationTime);
            
            // posición de desplazamiento: [0, 1]
            var position = (finished) ? 1 : elapsed / options.animationTime;
            
            // Facil [optional]
            if (options.pulseAlgorithm) {
                position = pulse(position);
            }
            
            // solo necesita la diferencia
            var x = (item.x * position - item.lastX) >> 0;
            var y = (item.y * position - item.lastY) >> 0;
            
            // agregar esto al desplazamiento total
            scrollX += x;
            scrollY += y;            
            
            // actualizar los últimos valores
            item.lastX += x;
            item.lastY += y;
        
            // eliminar y dar un paso atrás si se ha terminado
            if (finished) {
                que.splice(i, 1); i--;
            }           
        }

        // desplazarse hacia la izquierda y arriba
        if (scrollWindow) {
            window.scrollBy(scrollX, scrollY);
        } 
        else {
            if (scrollX) elem.scrollLeft += scrollX;
            if (scrollY) elem.scrollTop  += scrollY;                    
        }
        
        // limpia si no hay nada más que hacer
        if (!left && !top) {
            que = [];
        }
        
        if (que.length) { 
            requestFrame(step, elem, (delay / options.frameRate + 1)); 
        } else { 
            pending = false;
        }
    };
    
    // comenzar una nueva cola de acciones
    requestFrame(step, elem, 0);
    pending = true;
}


/***********************************************
 * Eventos
 ***********************************************/

/**
 * Manejador de la rueda del mouse.
 * @param {Object} event
 */
function wheel(event) {

    if (!initDone) {
        init();
    }
    
    var target = event.target;
    var overflowing = overflowingAncestor(target);
    
    // usar por defecto si no hay desbordamiento
    // elemento o acción predeterminada se previene   
    if (!overflowing || event.defaultPrevented ||
        isNodeName(activeElement, "embed") ||
       (isNodeName(target, "embed") && /\.pdf/i.test(target.src))) {
        return true;
    }

    var deltaX = event.wheelDeltaX || 0;
    var deltaY = event.wheelDeltaY || 0;
    
    // usa wheelDelta si delta X/Y no están disponibles
    if (!deltaX && !deltaY) {
        deltaY = event.wheelDelta || 0;
    }

    // comprobar si se trata de un desplazamiento del panel táctil que debe ser ignorado
    if (!options.touchpadSupport && isTouchpad(deltaY)) {
        return true;
    }

    // escala por tamaño
    // delta es 120 la mayor parte del tiempo
    // synaptics solo se envia 1 veces
    if (Math.abs(deltaX) > 1.2) {
        deltaX *= options.stepSize / 120;
    }
    if (Math.abs(deltaY) > 1.2) {
        deltaY *= options.stepSize / 120;
    }
    
    scrollArray(overflowing, -deltaX, -deltaY);
    event.preventDefault();
}

/**
 * Controlador de eventos Keydown.
 * @param {Object} event
 */
function keydown(event) {

    var target   = event.target;
    var modifier = event.ctrlKey || event.altKey || event.metaKey || 
                  (event.shiftKey && event.keyCode !== key.spacebar);
    
    // no hacer nada si el usuario está editando texto
    // o usando una tecla mientras modifica (excepto cambio)
    // o en un menú desplegable
    if ( /input|textarea|select|embed/i.test(target.nodeName) ||
         target.isContentEditable || 
         event.defaultPrevented   ||
         modifier ) {
      return true;
    }
    // la barra espaciadora debe presionar un botón
    if (isNodeName(target, "button") &&
        event.keyCode === key.spacebar) {
      return true;
    }
    
    var shift, x = 0, y = 0;
    var elem = overflowingAncestor(activeElement);
    var clientHeight = elem.clientHeight;

    if (elem == document.body) {
        clientHeight = window.innerHeight;
    }

    switch (event.keyCode) {
        case key.up:
            y = -options.arrowScroll;
            break;
        case key.down:
            y = options.arrowScroll;
            break;         
        case key.spacebar: // (+ shift)
            shift = event.shiftKey ? 1 : -1;
            y = -shift * clientHeight * 0.9;
            break;
        case key.pageup:
            y = -clientHeight * 0.9;
            break;
        case key.pagedown:
            y = clientHeight * 0.9;
            break;
        case key.home:
            y = -elem.scrollTop;
            break;
        case key.end:
            var damt = elem.scrollHeight - elem.scrollTop - clientHeight;
            y = (damt > 0) ? damt+10 : 0;
            break;
        case key.left:
            x = -options.arrowScroll;
            break;
        case key.right:
            x = options.arrowScroll;
            break;            
        default:
            return true; // una clave que no es importante
    }

    scrollArray(elem, x, y);
    event.preventDefault();
}

/**
 * Evento de Mousedown solo para actualizar activeElement
 */
function mousedown(event) {
    activeElement = event.target;
}


/***********************************************
 * REBOSAR
 ***********************************************/
 
var cache = {}; // limpiado de vez en cuando
setInterval(function () { cache = {}; }, 10 * 1000);

var uniqueID = (function () {
    var i = 0;
    return function (el) {
        return el.uniqueID || (el.uniqueID = i++);
    };
})();

function setCache(elems, overflowing) {
    for (var i = elems.length; i--;)
        cache[uniqueID(elems[i])] = overflowing;
    return overflowing;
}

function overflowingAncestor(el) {
    var elems = [];
    var rootScrollHeight = root.scrollHeight;
    do {
        var cached = cache[uniqueID(el)];
        if (cached) {
            return setCache(elems, cached);
        }
        elems.push(el);
        if (rootScrollHeight === el.scrollHeight) {
            if (!isFrame || root.clientHeight + 10 < rootScrollHeight) {
                return setCache(elems, document.body); // desplazamiento de la raíz en WebKit
            }
        } else if (el.clientHeight + 10 < el.scrollHeight) {
            overflow = getComputedStyle(el, "").getPropertyValue("overflow-y");
            if (overflow === "scroll" || overflow === "auto") {
                return setCache(elems, el);
            }
        }
    } while (el = el.parentNode);
}


/***********************************************
 * AYUDANTES
 ***********************************************/

function addEvent(type, fn, bubble) {
    window.addEventListener(type, fn, (bubble||false));
}

function removeEvent(type, fn, bubble) {
    window.removeEventListener(type, fn, (bubble||false));  
}

function isNodeName(el, tag) {
    return (el.nodeName||"").toLowerCase() === tag.toLowerCase();
}

function directionCheck(x, y) {
    x = (x > 0) ? 1 : -1;
    y = (y > 0) ? 1 : -1;
    if (direction.x !== x || direction.y !== y) {
        direction.x = x;
        direction.y = y;
        que = [];
        lastScroll = 0;
    }
}

var deltaBufferTimer;

function isTouchpad(deltaY) {
    if (!deltaY) return;
    deltaY = Math.abs(deltaY)
    deltaBuffer.push(deltaY);
    deltaBuffer.shift();
    clearTimeout(deltaBufferTimer);

    var allEquals    = (deltaBuffer[0] == deltaBuffer[1] && 
                        deltaBuffer[1] == deltaBuffer[2]);
    var allDivisable = (isDivisible(deltaBuffer[0], 120) &&
                        isDivisible(deltaBuffer[1], 120) &&
                        isDivisible(deltaBuffer[2], 120));
    return !(allEquals || allDivisable);
} 

function isDivisible(n, divisor) {
    return (Math.floor(n / divisor) == n / divisor);
}

var requestFrame = (function () {
      return  window.requestAnimationFrame       || 
              window.webkitRequestAnimationFrame || 
              function (callback, element, delay) {
                  window.setTimeout(callback, delay || (1000/60));
              };
})();


/***********************************************
 * LEGUMBRES
 ***********************************************/
 
/**
 * Fluido viscoso con pulso por parte y descomposición por el resto.
 * - Aplica una fuerza fija sobre un intervalo (una aceleración amortiguada), y
 * - Permite que el exponencial purgue la velocidad en un intervalo más largo
 * <Federico Carrizo - Konnor Desing's> *
 */
function pulse_(x) {
    var val, start, expx;
    // Prueba
    x = x * options.pulseScale;
    if (x < 1) { // aceleración
        val = x - (1 - Math.exp(-x));
    } else {     // tail
        // la animación anterior terminó aquí:
        start = Math.exp(-1);
        // arrastre viscoso simple
        x -= 1;
        expx = 1 - Math.exp(-x);
        val = start + (expx * (1 - start));
    }
    return val * options.pulseNormalize;
}

function pulse(x) {
    if (x >= 1) return 1;
    if (x <= 0) return 0;

    if (options.pulseNormalize == 1) {
        options.pulseNormalize /= pulse_(1);
    }
    return pulse_(x);
}

var isChrome = /chrome/i.test(window.navigator.userAgent);
var isMouseWheelSupported = 'onmousewheel' in document; 

if (isMouseWheelSupported && isChrome) {
	addEvent("mousedown", mousedown);
	addEvent("mousewheel", wheel);
	addEvent("load", init);
};

})();